float mesureVal(uint8_t pin){
	uint16_t buffer[13];
	for(uint8_t ind= 0; ind<ARRAY_LENGTH(buffer); ind++){
		uint16_t v = analogRead(pin);
		uint8_t i = ind;
		while (i>0 && buffer[i-1]>v){
			buffer[i] = buffer[i-1];
			i--;
		}
		buffer[i] = v;
	}
	return ADC_LUT[buffer[(uint8_t)ARRAY_LENGTH(buffer)/2]];
}

float mesureValSimple(uint8_t pin){
	return ADC_LUT[analogRead(pin)];
}

float readTemp(uint8_t pin){
	float adc = mesureValSimple(pin);
	float Vout = adc * VSS/ADCMAX;
  	float Rt = R1 * Vout / (VSS - Vout);

	float T = 1/(1/T0 + log(Rt/R0)/BETA);    // Temperature in Kelvin
	float Tc = T - 273.15;                   // Celsius
	return Tc;
}

void setup() {
	
	DEBUGINIT();
	
	loadIP();
	WiFi.begin();

	DEBUGLOG("Setup\nWait WiFi for 30s :");
	uint32_t mem = millis();
	while((millis() - mem)<30000 && !WiFi.isConnected()){
		delay(1);
	}

	if (!WiFi.isConnected()){
		DEBUGLOG("\nStart SmartConfig for 60s :");
		WiFi.beginSmartConfig();
		mem = millis();
		while ((millis() - mem)<60000 && !WiFi.smartConfigDone()){
			delay(1000);
			DEBUGLOG("/");
		}
		if (WiFi.smartConfigDone()){
			DEBUGLOG("\nSmartConfig Done\n");
			WiFi.begin(WiFi.SSID().c_str(),WiFi.psk().c_str(),WiFi.channel(),WiFi.BSSID());
			delay(1000);
			ESP.restart();
		}
		else
			DEBUGLOG("\nSmartConfig Abort, continue without WiFi\n");
	}
	
	IPAddress myIP = WiFi.localIP();
	IPAddress myMask = WiFi.subnetMask();
	for (int i = 0; i < 4; i++)
		broadcastIP[i] = (myIP[i] & myMask[i]) | (~myMask[i]);
	DEBUGLOG("\nIPLocal %s\nMask %s\nBroadCast %s\n", myIP.toString().c_str(),myMask.toString().c_str(),broadcastIP.toString().c_str());

	// udpTeleplot.begin(PORTTELEPLOT);
	udpNR.begin(PORTNR);

	ArduinoOTA.begin();
	dht.setup(4, DHTesp::DHT22);  // Connect DHT sensor to GPIO 4

    dac_output_enable(DAC_CHANNEL_1);    // pin 25
    dac_output_voltage(DAC_CHANNEL_1, 0);
    analogReadResolution(12);
}

// char buffer [20];
char buffer [100];
float memTemperature = 0;
float memHumidity = 0;
uint32_t memMillis = 0;
uint8_t output = 125;

void loop() {
	// output++;
	// dac_output_voltage(DAC_CHANNEL_1, output);
	// delayMicroseconds(100);

	// DEBUGLOG(">Consigne:%1.3f\n>Mesure1:%1.3f\n>Mesure2:%1.3f\n",output*16*3.3/4096, mesureVal(ADC_PIN1)*3.3/4096, mesureVal(ADC_PIN2)*3.3/4096);
	// DEBUGLOG(">Consigne:%3.1f\n>RAW:%d\n>Mesure:%3.1f\n",output*16*3.3, val, ADC_LUT[val]);1
	float tempRaw1 = readTemp(ADC_PIN1);
	float tempRaw2 = readTemp(ADC_PIN2);
	float temp1 = simpleKalmanFilter1.updateEstimate(tempRaw1);
	float temp2 = simpleKalmanFilter2.updateEstimate(tempRaw2);

	DEBUGLOG(">TempRaw1:%3.3f\n>TempRaw2:%3.3f\n>Temp1:%3.3f\n>Temp2:%3.3f\n",tempRaw1, tempRaw2, temp1, temp2 );

	if (millis() - memMillis>300000){	
	// delay(dht.getMinimumSamplingPeriod());
		TempAndHumidity tempAndHum = dht.getTempAndHumidity();
		if (dht.getStatus() == DHTesp::DHT_ERROR_t::ERROR_NONE){
			DEBUGLOG("*");
			memMillis = millis();
			memHumidity = tempAndHum.humidity;
			memTemperature = tempAndHum.temperature;
			sprintf(buffer, "{\"temperature\":%3.1f,\"humidity\":%3.1f,\"temp1\":%3.1f,\"temp2\":%3.1f}\0", round(tempAndHum.temperature*10)/10, round(tempAndHum.humidity*10)/10, temp1, temp2);
			udpNR.beginPacket(broadcastIP, PORTNR);
			udpNR.print(buffer);
			udpNR.endPacket();
		}
		else 
			DEBUGLOG("E %d",dht.getStatus());
	}
	ArduinoOTA.handle();
	delay(1000);	
}
